import React from 'react';

export default class PersonalDataForm extends React.Component {
    constructor (props) {
        super(props);
        this.props = {
            fieldRequired: {
                    name: true,
                    nickName: false,
                    email: true,
                    field: true,
                    position: true,
                },
                errorMsgs: {
                    name: 'Please fill in your name',
                    nickName: '',
                    email: 'Please use a correct e-mail address',
                    field: 'Please choose your field',
                    position: 'Please choose your position',
                },
                validationRules: {
                    name: this.value.trim().length,
                    nickName: true,
                    email: this.value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i),
                    field: this.value.trim().length,
                    position: this.value.trim().length,
                },
        }
        this.state = {
            name: '',
                nickName: '',
                email: '',
                field: '',
                position: '',
                fieldValid:{
                    name: null,
                    nickName: null,
                    email: null,
                    field: null,
                    position: null,
                }
        }
        this.updateState = this.updateState.bind(this);
        this.switchSelects = this.switchSelects.bind(this);
    }

    validateField () {

    }

    updateState (e) {
        const {name, value} = e.target;
        this.setState({[name]: value})
    }

    switchSelects () {
        switch(this.state.field){
            case  'IT':
                return (
                    <select name="position" id="position" onChange={this.updateState}>
                        <option value="">{this.state.position ? 'Go back to Field' : 'Position'}</option>
                        <option value="Front-end developer">Front-end developer</option>
                        <option value="Back-end developer">Back-end developer</option>
                        <option value="DevOps">DevOps</option>
                        <option value="Front-end developer">Webmaster</option>
                    </select>
                );
            case 'Product':
                return (
                     <select name="position" id="position" onChange={this.updateState}>
                        <option value="">{this.state.position ? 'Go back to Field' : 'Position'}</option>
                        <option value="Product Owner">Product Owner</option>
                        <option value="UX Designer">UX Designer</option>
                        <option value="UI Designer">UI Designer</option>
                    </select>
                );
             case 'Content':
                return (
                     <select name="position" id="position" onChange={this.updateState}>
                        <option value="">{this.state.position ? 'Go back to Field' : 'Position'}</option>
                        <option value="Junior Copywriter">Junior Copywriter</option>
                        <option value="Senior Copywriter">Senior Copywriter</option>
                    </select>
                );

            default:
                return('');
        }
    }

    render() {
        return (
            <div>
                <form action="" id="personal-data-form">
                    <div className="row">
                        <input name="name" type="text" noValidate onBlur={this.updateState}/>
                        <label htmlFor="name">Name</label>
                    </div>
                    <div className="row">
                        <input name="nickName" type="text" noValidate onBlur={this.updateState}/>
                        <label htmlFor="nickName">Nickname</label>
                    </div>
                    <div className="row">
                        <input name="email" type="email" noValidate onBlur={this.updateState}/>
                        <label htmlFor="email">E-mail</label>
                    </div>
                    <div className="row">
                        <select name="field" id="field" onChange={this.updateState} disabled={this.state.position !== ''}>
                            <option value="">Field</option>
                            <option value="IT">IT</option>
                            <option value="Product">Product</option>
                            <option value="Content">Content</option>
                        </select>
                    </div>
                    <div className="row">
                        {this.switchSelects()}
                    </div>



                    <input id="submit-button" type="submit" value="Submit" />
                </form>
                <ol>
                    <li>Name: {this.state.name}</li>
                    <li>Nickname: {this.state.nickName}</li>
                    <li>E-mail: {this.state.email}</li>
                    <li>Field: {this.state.field}</li>
                    <li>Position: {this.state.position}</li>
                </ol>
            </div>



        )
    }
}
